// --- Frontend: ReactJS ---
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [cells, setCells] = useState([]);
  const [editing, setEditing] = useState(null);
  const [value, setValue] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/cells').then((res) => setCells(res.data));
  }, []);

  const handleSave = async (row, col) => {
    const existingCell = cells.find((cell) => cell.row === row && cell.col === col);
    if (existingCell) {
      await axios.put(`http://localhost:5000/cells/${existingCell._id}`, { value });
    } else {
      const res = await axios.post('http://localhost:5000/cells', { row, col, value });
      setCells([...cells, res.data]);
    }
    setEditing(null);
  };

  return (
    <div className="App">
      <h1>Interactive Spreadsheet Application</h1>
      <table>
        <tbody>
          {[...Array(10)].map((_, row) => (
            <tr key={row}>
              {[...Array(10)].map((_, col) => {
                const cell = cells.find((c) => c.row === row && c.col === col);
                return (
                  <td key={col} onClick={() => setEditing(`${row}-${col}`)}>
                    {editing === `${row}-${col}` ? (
                      <input
                        value={value}
                        onChange={(e) => setValue(e.target.value)}
                        onBlur={() => handleSave(row, col)}
                      />
                    ) : (
                      cell ? cell.value : ''
                    )}
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
