import React, { useState, useEffect } from 'react';
import './App.css'; // Import the external CSS file

const App = () => {
  const [cells, setCells] = useState([]);
  const [editing, setEditing] = useState({ row: null, col: null, value: '' });
  const [gridSize, setGridSize] = useState({ rows: 20, cols: 20 }); // Default size (20x20)
  const [history, setHistory] = useState([]); // History for undo/redo
  const [currentHistoryIndex, setCurrentHistoryIndex] = useState(-1); // Track history position

  // Fetch all cells on page load
  useEffect(() => {
    const fetchCells = async () => {
      const response = await fetch('http://localhost:5000/cells');
      if (response.ok) {
        const data = await response.json();
        setCells(data);
      } else {
        console.error('Failed to fetch cells');
      }
    };
    fetchCells();
  }, []);

  // Handle cell value change
  const handleCellChange = (e, row, col) => {
    setEditing({ row, col, value: e.target.value });
  };

  // Handle save/update cell value
  const handleCellSave = async (row, col) => {
    const { value } = editing;
    if (!value) return;

    const response = await fetch('http://localhost:5000/cells', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ row, col, value }),
    });

    if (response.ok) {
      const updatedCell = await response.json();
      setCells((prevCells) => {
        return prevCells.map((cell) => {
          if (cell.row === updatedCell.row && cell.col === updatedCell.col) {
            return updatedCell;
          }
          return cell;
        });
      });

      // Add the change to history for undo/redo functionality
      addToHistory();
      setEditing({ row: null, col: null, value: '' });
    } else {
      console.error('Failed to save cell');
    }
  };

  // Add the current state of cells to history (for undo/redo)
  const addToHistory = () => {
    const newHistory = [...history.slice(0, currentHistoryIndex + 1), [...cells]];
    setHistory(newHistory);
    setCurrentHistoryIndex(newHistory.length - 1);
  };

  // Undo the last change
  const handleUndo = () => {
    if (currentHistoryIndex > 0) {
      const previousCells = history[currentHistoryIndex - 1];
      setCells(previousCells);
      setCurrentHistoryIndex(currentHistoryIndex - 1);
    }
  };

  // Redo the last undone change
  const handleRedo = () => {
    if (currentHistoryIndex < history.length - 1) {
      const nextCells = history[currentHistoryIndex + 1];
      setCells(nextCells);
      setCurrentHistoryIndex(currentHistoryIndex + 1);
    }
  };

  // Handle grid resizing (add or remove rows/columns)
  const handleResizeGrid = (direction, type) => {
    if (type === 'row') {
      setGridSize((prev) => {
        const newRows = direction === 'add' ? prev.rows + 1 : Math.max(1, prev.rows - 1);
        return { ...prev, rows: newRows };
      });
    } else if (type === 'col') {
      setGridSize((prev) => {
        const newCols = direction === 'add' ? prev.cols + 1 : Math.max(1, prev.cols - 1);
        return { ...prev, cols: newCols };
      });
    }
  };

  // Sorting functionality (sort by column)
  const handleSort = (colIndex) => {
    const sortedCells = [...cells];
    sortedCells.sort((a, b) => {
      if (a.col === colIndex && b.col === colIndex) {
        return a.value > b.value ? 1 : -1;
      }
      return 0;
    });
    setCells(sortedCells);
  };

  // Render the spreadsheet grid
  const renderGrid = () => {
    const grid = [];
    for (let row = 0; row < gridSize.rows; row++) {
      const cellsInRow = [];
      for (let col = 0; col < gridSize.cols; col++) {
        const cell = cells.find((cell) => cell.row === row && cell.col === col);
        cellsInRow.push(
          <td
            key={`${row}-${col}`}
            onClick={() => setEditing({ row, col, value: cell ? cell.value : '' })}
            className="cell"
            style={{
              border: '1px solid #ccc',
              textAlign: 'center',
              backgroundColor: (row + col) % 2 === 0 ? '#f4f4f4' : '#fff', // Alternating row colors
            }}
          >
            {editing.row === row && editing.col === col ? (
              <input
                type="text"
                value={editing.value}
                onChange={(e) => handleCellChange(e, row, col)}
                onBlur={() => handleCellSave(row, col)}
                autoFocus
                className="cell-input"
                style={{ width: '100%', height: '100%' }}
              />
            ) : (
              <span>{cell ? cell.value : ''}</span>
            )}
          </td>
        );
      }
      grid.push(<tr key={row}>{cellsInRow}</tr>);
    }
    return grid;
  };

  return (
    <div className="spreadsheet-container">
      <h1>Interactive Spreadsheet</h1>
      
      {/* Buttons for Undo and Redo */}
      <button onClick={handleUndo} disabled={currentHistoryIndex === 0}>Undo</button>
      <button onClick={handleRedo} disabled={currentHistoryIndex === history.length - 1}>Redo</button>

      {/* Resize Grid Buttons */}
      <div>
        <button onClick={() => handleResizeGrid('add', 'row')}>Add Row</button>
        <button onClick={() => handleResizeGrid('remove', 'row')}>Remove Row</button>
        <button onClick={() => handleResizeGrid('add', 'col')}>Add Column</button>
        <button onClick={() => handleResizeGrid('remove', 'col')}>Remove Column</button>
      </div>

      {/* Table with Sorting */}
      <table className="spreadsheet">
        <thead>
          <tr>
            <th></th>
            {[...Array(gridSize.cols)].map((_, colIndex) => (
              <th key={colIndex} onClick={() => handleSort(colIndex)}>
                {String.fromCharCode(65 + colIndex)}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>{renderGrid()}</tbody>
      </table>
    </div>
  );
};

export default App;
